// ==UserScript==
// @name         Liquid Glass Injector
// @namespace    https://github.com/ZiuChen
// @version      1.1.0
// @description  Injects a liquid glass effect element into the page via menu command.
// @author       ZiuChen
// @homepage     https://github.com/ZiuChen
// @supportURL   https://github.com/ZiuChen/userscript/issues
// @match        *://*/*
// @icon         data:image/svg+xml;base64,
// @grant        GM_registerMenuCommand
// @grant        GM_setValue
// @grant        GM_getValue
// @grant        GM_addValueChangeListener
// @grant        GM_removeValueChangeListener
// @run-at       document-end
// @updateURL    https://cdn.jsdelivr.net/gh/ZiuChen/userscript@main/src/userscript/liquid-glass-injector/index.user.js
// @downloadURL  https://cdn.jsdelivr.net/gh/ZiuChen/userscript@main/src/userscript/liquid-glass-injector/index.user.js
// ==/UserScript==

const IMAGES = {
  border:
    'data:image/png;base64,',
  displacement_1:
    'data:image/png;base64,',
  displacement_2:
    'data:image/png;base64,'
}

;(function () {
  'use strict'

  // 
  const CONFIG = {
    width: 210,
    height: 150,
    displacement1: IMAGES['displacement_1'],
    displacement2: IMAGES['displacement_2'],
    borderImg: IMAGES['border'],
    zIndex: 99999
  }

  const POSITION_KEY = 'liquidGlassPosition'
  const ACTIVE_KEY = 'liquidGlassActive'

  let injected = false // 
  let suppressLocalSync = false
  let suppressActiveSync = false
  let activeListenerId = null
  let currentInstance = null
  let beforeUnloadCleanupRegistered = false

  init()

  function init() {
    registerMenus()
    setupActiveSync()
    const initialActive = typeof GM_getValue === 'function' ? GM_getValue(ACTIVE_KEY, true) : true
    if (initialActive) {
      applyActiveState(true)
    }
  }

  function registerMenus() {
    if (typeof GM_registerMenuCommand !== 'function') {
      return
    }
    GM_registerMenuCommand(' (Liquid Glass)', () => {
      requestActivation(true)
    })
    GM_registerMenuCommand(' (Liquid Glass)', () => {
      requestActivation(false)
    })
  }

  function setupActiveSync() {
    if (typeof GM_addValueChangeListener !== 'function') return
    activeListenerId = GM_addValueChangeListener(
      ACTIVE_KEY,
      (_name, _oldValue, newValue, remote) => {
        if (remote === false && suppressActiveSync) return
        applyActiveState(Boolean(newValue))
      }
    )
    if (
      !beforeUnloadCleanupRegistered &&
      typeof window !== 'undefined' &&
      typeof GM_removeValueChangeListener === 'function'
    ) {
      beforeUnloadCleanupRegistered = true
      window.addEventListener('beforeunload', () => {
        if (activeListenerId !== null) {
          GM_removeValueChangeListener(activeListenerId)
          activeListenerId = null
        }
      })
    }
  }

  function requestActivation(active) {
    const normalized = Boolean(active)
    if (typeof GM_setValue === 'function') {
      suppressActiveSync = true
      GM_setValue(ACTIVE_KEY, normalized)
      setTimeout(() => {
        suppressActiveSync = false
      }, 0)
    }
    applyActiveState(normalized)
  }

  function applyActiveState(active) {
    if (active) {
      injectLiquidGlass()
    } else {
      removeLiquidGlass()
    }
  }

  function injectLiquidGlass() {
    if (injected) return
    injected = true
    currentInstance = createLiquidGlassInstance()
  }

  function removeLiquidGlass() {
    if (!injected || !currentInstance) return
    currentInstance.destroy()
    currentInstance = null
    injected = false
  }

  function createLiquidGlassInstance() {
    let positionListenerId = null
    let localBeforeUnloadHandler = null
    let isDragging = false
    let startX = 0
    let startY = 0
    let initialLeft = 0
    let initialTop = 0
    let currentPosition = null

    // 1.  shadow root 
    const host = document.createElement('div')
    host.setAttribute('data-liquid-glass-host', 'true')
    document.body.appendChild(host)
    const shadow = host.attachShadow({ mode: 'open' })

    const style = document.createElement('style')
    style.textContent = `
            .liquid-glass-container {
                position: fixed;
                top: 20px;
                left: 20px;
                width: ${CONFIG.width}px;
                height: ${CONFIG.height}px;
                z-index: ${CONFIG.zIndex};
                user-select: none;
                cursor: grab;
                opacity: 0;
                transition: opacity 0.5s ease, transform 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.275);
            }
            .liquid-glass-container.visible {
                opacity: 1;
            }
            .liquid-glass-container:active {
                cursor: grabbing;
                transform: scale(1.1);
            }
            .liquid-glass-content {
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                display: flex;
                align-items: center;
                justify-content: center;
                border-radius: 9999px;
                font-family: monospace;
                font-size: 16px;
                color: #333;
                font-weight: bold;
                backdrop-filter: url(#magnifying-glass-filter);
                -webkit-backdrop-filter: url(#magnifying-glass-filter);
                box-shadow: rgba(0, 0, 0, 0.05) 0px 4px 9px,
                            rgba(0, 0, 0, 0.05) 0px 2px 24px inset,
                            rgba(255, 255, 255, 0.2) 0px -2px 24px inset;
            }
        `
    shadow.appendChild(style)

    // 2. 
    const container = document.createElement('div')
    container.className = 'liquid-glass-container'

    // 3.  SVG Filter  ()
    const svgDefs = document.createElementNS('http://www.w3.org/2000/svg', 'svg')
    svgDefs.setAttribute('style', 'display: none;')
    svgDefs.setAttribute('color-interpolation-filters', 'sRGB')
    svgDefs.innerHTML = `
            <defs>
                <filter id="magnifying-glass-filter">
                    <feImage href="${CONFIG.displacement1}" x="0" y="0" width="${CONFIG.width}" height="${CONFIG.height}" result="magnifying_displacement_map" />
                    <feDisplacementMap
                        in="SourceGraphic"
                        in2="magnifying_displacement_map"
                        scale="24"
                        xChannelSelector="R"
                        yChannelSelector="G"
                        result="magnified_source"
                    />
                    <feGaussianBlur in="magnified_source" stdDeviation="0" result="blurred_source" />
                    <feImage href="${CONFIG.displacement2}" x="0" y="0" width="${CONFIG.width}" height="${CONFIG.height}" result="displacement_map" />
                    <feDisplacementMap
                        in="blurred_source"
                        in2="displacement_map"
                        scale="98.24713343067756"
                        xChannelSelector="R"
                        yChannelSelector="G"
                        result="displaced"
                    />
                    <feColorMatrix in="displaced" type="saturate" result="displaced_saturated" values="9"></feColorMatrix>
                    <feImage href="${CONFIG.borderImg}" x="0" y="0" width="${CONFIG.width}" height="${CONFIG.height}" result="specular_layer"></feImage>
                    <feComposite in="displaced_saturated" in2="specular_layer" operator="in" result="specular_saturated"></feComposite>
                    <feComponentTransfer in="specular_layer" result="specular_faded">
                        <feFuncA type="linear" slope="0.5"></feFuncA>
                    </feComponentTransfer>
                    <feBlend in="specular_saturated" in2="displaced" mode="normal" result="withSaturation"></feBlend>
                    <feBlend in="specular_faded" in2="withSaturation" mode="normal"></feBlend>
                </filter>
            </defs>
        `

    // 4. 
    const content = document.createElement('div')
    content.className = 'liquid-glass-content'
    content.innerText = 'Liquid Grass'

    //  DOM
    container.appendChild(svgDefs)
    container.appendChild(content)
    shadow.appendChild(container)

    const defaultPosition = { left: 20, top: 20 }
    const storedPosition = getStoredPosition()
    currentPosition = storedPosition || defaultPosition
    applyPosition(currentPosition)

    // 5. 
    requestAnimationFrame(() => {
      container.classList.add('visible')
    })

    // 7. 

    function onMouseDown(e) {
      // 
      if (e.button !== 0) return
      isDragging = true
      content.innerText = ''

      startX = e.clientX
      startY = e.clientY

      const rect = container.getBoundingClientRect()
      initialLeft = rect.left
      initialTop = rect.top
      e.preventDefault()
      document.addEventListener('mousemove', onMouseMove)
      document.addEventListener('mouseup', onMouseUp)
    }

    function onMouseMove(e) {
      if (!isDragging) return

      const dx = e.clientX - startX
      const dy = e.clientY - startY
      let newLeft = initialLeft + dx
      let newTop = initialTop + dy
      const maxLeft = window.innerWidth - CONFIG.width
      const maxTop = window.innerHeight - CONFIG.height
      newLeft = Math.max(0, Math.min(newLeft, maxLeft))
      newTop = Math.max(0, Math.min(newTop, maxTop))
      container.style.left = `${newLeft}px`
      container.style.top = `${newTop}px`
      container.style.bottom = 'auto'
      container.style.right = 'auto'
      currentPosition = { left: newLeft, top: newTop }
      persistPosition(currentPosition)
    }

    function onMouseUp() {
      if (!isDragging) return
      isDragging = false
      document.removeEventListener('mousemove', onMouseMove)
      document.removeEventListener('mouseup', onMouseUp)
      // 
      content.innerText = 'Liquid Grass'

      persistPosition(currentPosition)
    }

    container.addEventListener('mousedown', onMouseDown)

    if (typeof GM_addValueChangeListener === 'function') {
      positionListenerId = GM_addValueChangeListener(
        POSITION_KEY,
        (_name, _oldValue, newValue, remote) => {
          if (!newValue) return
          if (isDragging) return
          if (remote === false && suppressLocalSync) return
          const sanitized = sanitizePosition(newValue)
          if (!sanitized) return
          if (
            Math.abs(sanitized.left - currentPosition.left) < 0.5 &&
            Math.abs(sanitized.top - currentPosition.top) < 0.5
          ) {
            return
          }
          applyPosition(sanitized)
        }
      )
    }

    if (typeof window !== 'undefined' && typeof GM_removeValueChangeListener === 'function') {
      localBeforeUnloadHandler = () => {
        if (positionListenerId !== null) {
          GM_removeValueChangeListener(positionListenerId)
          positionListenerId = null
        }
      }
      window.addEventListener('beforeunload', localBeforeUnloadHandler)
    }

    function applyPosition(position) {
      if (!position) return
      const clamped = clampPosition(position)
      currentPosition = clamped
      container.style.left = `${clamped.left}px`
      container.style.top = `${clamped.top}px`
      container.style.bottom = 'auto'
      container.style.right = 'auto'
    }

    function destroy() {
      document.removeEventListener('mousemove', onMouseMove)
      document.removeEventListener('mouseup', onMouseUp)
      container.removeEventListener('mousedown', onMouseDown)
      if (positionListenerId !== null && typeof GM_removeValueChangeListener === 'function') {
        GM_removeValueChangeListener(positionListenerId)
        positionListenerId = null
      }
      if (localBeforeUnloadHandler && typeof window !== 'undefined') {
        window.removeEventListener('beforeunload', localBeforeUnloadHandler)
      }
      host.remove()
    }

    return {
      destroy
    }
  }

  function getStoredPosition() {
    if (typeof GM_getValue !== 'function') return null
    const value = GM_getValue(POSITION_KEY, null)
    return sanitizePosition(value)
  }

  function persistPosition(position) {
    if (typeof GM_setValue !== 'function') return
    if (!position) return
    suppressLocalSync = true
    GM_setValue(POSITION_KEY, {
      left: position.left,
      top: position.top
    })
    setTimeout(() => {
      suppressLocalSync = false
    }, 0)
  }

  function sanitizePosition(pos) {
    if (!pos || typeof pos !== 'object') return null
    const left = Number(pos.left)
    const top = Number(pos.top)
    if (!Number.isFinite(left) || !Number.isFinite(top)) return null
    return clampPosition({ left, top })
  }

  function clampPosition(pos) {
    const maxLeft = Math.max(0, window.innerWidth - CONFIG.width)
    const maxTop = Math.max(0, window.innerHeight - CONFIG.height)
    return {
      left: Math.min(Math.max(pos.left, 0), maxLeft),
      top: Math.min(Math.max(pos.top, 0), maxTop)
    }
  }
})()
